export function extractCharacters(str: string): string {
    if (!isNaN(Number(str[0]))) {
      // If the first character is a number, extract it and return as a string
      return str[0];
    } else if (str[0] === '+' && str.length > 1 && !isNaN(Number(str[1]))) {
      // If the first character is a '+', and the second character is a digit, extract the second character
      return str[1];
    } else if (str[0].match(/[a-zA-Z]/)) {
      // If the first character is a letter, extract it and convert to uppercase
      return str[0].toUpperCase();
    } else {
      // If the string does not match any pattern, extract it and return as a string
      return str[0];
    }
  }